    <div>
      <div>
        <button onClick={Post}>글쓰기</button>
      </div>
      <ul>
        {boardList.map((board) => (
          <li key={board.id}>
            <Link to={`/board/${board.id}`}>{board.title}</Link>
            {board.writingTime}
            {board.writer}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Board;
