import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/BoardDetail_style.css";
import { API_BASE_URL } from "../config.js";

const BoardDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [board, setBoard] = useState({});
  const [imageUrls, setImageUrls] = useState([]);
  const [imageName, setImageName] = useState("");
  const getBoard = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/board?writingId=${id}`);
      setBoard(response.data);
      console.log(response.data);
      console.log(board);

      const fileIds = response.data.fileIds;
      if (fileIds && fileIds.length > 0) {
        getFileUrls(fileIds);
      }
      console.log(fileIds);
      /*
      const urlsFromLocalStorage = localStorage.getItem(id);
      console.log("Local", urlsFromLocalStorage);
      if (urlsFromLocalStorage) {
        setImageUrls(JSON.parse(urlsFromLocalStorage));
      }
      console.log("imageUrls", imageUrls.length);
      if (imageUrls.length > 0) {
        requestUrl(imageUrls[0].substring(imageUrls[0].lastIndexOf("/") + 1));
      }
      */
    } catch (error) {
      console.error("불러오지 못함", error);
    }
  };

  const getFileUrls = async (fileIds) => {
    try {
      const urls = await Promise.all(
        fileIds.map(async (fileId) => {
          const response = await axios.get(`${API_BASE_URL}/file/${fileId}`);
          return response.data.url;
        })
      );
      setImageUrls(urls);
    } catch (error) {
      console.error("파일 URL을 가져오는 데 실패했습니다.", error);
    }
  };
  /*
  const requestUrl = async (fileUrl) => {
    console.log("haha ", fileUrl);
    setImageName(fileUrl);
    console.log("imageName :", imageName);

    try {
      const response = await axios.get(`${API_BASE_URL}/board${fileUrl}`);
      setImageName(fileUrl);
      console.log(imageName);
    } catch (error) {
      console.error("불러오지 못함", error);
    }
  };*/
  useEffect(() => {
    getBoard();
  }, [id]);

  const moveToEdit = () => {
    navigate("/edit/" + id);
  };

  const deletePost = async () => {
    if (window.confirm("게시글을 삭제하시겠습니까?")) {
      await axios.delete(`${API_BASE_URL}/board/${id}`);
      alert("삭제되었습니다.");
      navigate("/");
    }
  };

  const moveToBoard = () => {
    navigate("/");
  };

  return (
    <div className="board-detail-container">
      <div className="board-detail-content">
        <h2 className="board-detail-title">{board.title}</h2>
        <div className="board-detail-info">
          <h5>작성자 : {board.writer}</h5>
          <p style={{ fontSize: "12px", color: "gray" }}>{board.writingTime}</p>
        </div>
        <hr />
        <p className="board-detail-body">{board.body}</p>
        {imageUrls.map((url, index) => (
          <img key={index} src={url} alt={`Image ${index}`} />
        ))}
        <hr />
        <div className="board-detail-button-group">
          <button onClick={moveToEdit}>수정</button>
          <button onClick={deletePost}>삭제</button>
          <button onClick={moveToBoard}>목록</button>
        </div>
      </div>
    </div>
  );
};

export default BoardDetail;
