import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/PostForm_style.css";
import { API_BASE_URL } from "../config.js";

const PostForm = ({ addPost }) => {
  const navigate = useNavigate();
  const [writer, setWriter] = useState("");
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const savePost = async (e) => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/board`,
        {
          writer: writer,
          title: title,
          body: body,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      if (response.status === 200) {
        alert("게시물이 등록되었습니다.");
        navigate("/");
      } else {
        throw new Error("게시물 등록 실패");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("게시물 등록에 실패했습니다.");
    }
  };

  const backToBoard = () => {
    navigate("/");
  };

  const [uploadImg1Url, setUploadImg1Url] = useState("");
  const [uploadImg2Url, setUploadImg2Url] = useState("");

  const onChangeImageUpload = (e, setImgUrl) => {
    const { files } = e.target;
    const uploadFile = files[0];
    const reader = new FileReader();
    reader.readAsDataURL(uploadFile);
    reader.onloadend = () => {
      setImgUrl(reader.result);
    };
  };
  const insertImageIntoBody = (imageUrl) => {
    setBody((prevBody) => prevBody + `<img src="${imageUrl}" />`);
  };
  return (
    <div className="container">
      <div className="input-group">
        <h2 style={{ textAlign: "center" }}>글쓰기</h2>
        <span>제목</span>
        <input
          type="text"
          placeholder="제목"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>

      <div className="input-group">
        <span>작성자</span>
        <input
          type="text"
          placeholder="작성자"
          value={writer}
          onChange={(e) => setWriter(e.target.value)}
        />
      </div>

      <div>
        <textarea
          placeholder="내용"
          cols="100"
          rows="10"
          value={body}
          onChange={(e) => setBody(e.target.value)}
        />
        {uploadImg1Url && (
          <button onClick={() => insertImageIntoBody(uploadImg1Url)}>
            Insert Image 1 into Body
          </button>
        )}
        {uploadImg2Url && (
          <button onClick={() => insertImageIntoBody(uploadImg2Url)}>
            Insert Image 2 into Body
          </button>
        )}
      </div>
      <input
        type="file"
        accept="image/*"
        onChange={(e) => onChangeImageUpload(e, setUploadImg1Url)}
      />

      <input
        type="file"
        accept="image/*"
        onChange={(e) => onChangeImageUpload(e, setUploadImg2Url)}
      />

      <div className="button-group">
        <button onClick={savePost}>저장</button>
        <button onClick={backToBoard}>취소</button>
      </div>
    </div>
  );
};

export default PostForm;
