import React from "react";

const Post = ({ post, deletePost }) => {
  const { id, author, title, content, timestamp } = post;

  const handleDelete = () => {
    deletePost(id);
  };

  return (
    <div>
      <h2>{title}</h2>
      <p>글쓴이: {author}</p>
      <p>작성시간: {timestamp}</p>
      <p>{content}</p>
      <button onClick={handleDelete}>삭제</button>
    </div>
  );
};

export default Post;
