import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Board = () => {
  const navigate = useNavigate();
  const [boardList, setBoardList] = useState([]);
  const [perPage, setPerPage] = useState(10); // 기본으로 보여질 게시글 수
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지

  const getBoardList = async () => {
    try {
      const response = await axios.get("http://localhost:8080/main");
      const responseData = response.data;

      setBoardList(responseData.data);
    } catch (error) {
      console.error("불러오지 못함", error);
    }
  };

  const handlePerPageChange = (e) => {
    setPerPage(parseInt(e.target.value));
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  useEffect(() => {
    getBoardList();
  }, []);

  const indexOfLastPost = currentPage * perPage;
  const indexOfFirstPost = indexOfLastPost - perPage;
  const currentPosts = boardList.slice(indexOfFirstPost, indexOfLastPost);
  const Pagination = ({ perPage, totalPosts, currentPage, onPageChange }) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalPosts / perPage); i++) {
      pageNumbers.push(i);
    }
    return (
      <div>
        <h2>게시판</h2>
        <div>
          <select value={perPage} onChange={handlePerPageChange}>
            <option value={10}>10개</option>
            <option value={20}>20개</option>
            <option value={30}>30개</option>
          </select>
        </div>
        <ul>
          {currentPosts.map((board) => (
            <li key={board.id}>
              <Link to={`/board/${board.id}`}>{board.title}</Link>
              {board.writingTime}
              {board.writer}
            </li>
          ))}
        </ul>
        <Pagination
          perPage={perPage}
          totalPosts={boardList.length}
          currentPage={currentPage}
          onPageChange={handlePageChange}
        />
      </div>
    );
  };

  return (
    <ul>
      {pageNumbers.map((number) => (
        <li key={number}>
          <button onClick={() => onPageChange(number)}>{number}</button>
        </li>
      ))}
    </ul>
  );
};

export default Board;
