import React, { useState } from "react";

const PostList = ({ posts }) => {
  const [pageSize, setPageSize] = useState(10);
  const [currentPage, setCurrentPage] = useState(1);
  const [expandedPostId, setExpandedPostId] = useState(null); // 확장된 글의 ID를 추적합니다.

  const handlePageSizeChange = (e) => {
    setPageSize(parseInt(e.target.value));
    setCurrentPage(1);
  };

  const totalPages = Math.ceil(posts.length / pageSize);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const togglePost = (postId) => {
    if (expandedPostId === postId) {
      setExpandedPostId(null); // 클릭한 글이 이미 확장된 상태면 숨김
    } else {
      setExpandedPostId(postId); // 클릭한 글을 확장
    }
  };

  return (
    <div>
      <label htmlFor="pageSize">게시글 수: </label>
      <select id="pageSize" value={pageSize} onChange={handlePageSizeChange}>
        <option value="10">10개</option>
        <option value="20">20개</option>
        <option value="30">30개</option>
      </select>
      <ul>
        {posts
          .slice((currentPage - 1) * pageSize, currentPage * pageSize)
          .map((post) => (
            <li key={post.id} onClick={() => togglePost(post.id)}>
              {/* 제목을 클릭하면 togglePost 함수를 호출하여 해당 글을 확장 또는 숨김 */}
              <h3>{post.title}</h3>
              {expandedPostId === post.id && <p>{post.content}</p>}{" "}
              {/* 확장된 글만 내용을 보여줌 */}
            </li>
          ))}
      </ul>
      <div>
        {Array.from({ length: totalPages }, (_, index) => index + 1).map(
          (page) => (
            <button key={page} onClick={() => handlePageChange(page)}>
              {page}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default PostList;
