import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const PostEditForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [post, setPost] = useState({
    writer: "",
    title: "",
    body: "",
  });

  const { writer, title, body } = post;

  const onChange = (event) => {
    const { value, name } = event.target;
    setPost({
      ...post,
      [name]: value,
    });
  };

  const getPost = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/board?writingId=${id}`
      );
      const responseData = await response.json();

      setPost(responseData.data);
    } catch (error) {
      console.error("Error fetching board:", error);
    }
  };
  const backToPost = () => {
    navigate("/board/" + id);
  };
  const updatePost = async () => {
    try {
      const response = await axios.put(
        `http://localhost:8080/board/${id}`,
        post
      );

      if (response.status === 200) {
        alert("수정되었습니다.");
        navigate("/board/" + id);
      } else {
        throw new Error("게시물 수정 실패");
      }
    } catch (error) {
      console.error("Error updating board:", error);
      alert("게시물 수정에 실패했습니다.");
    }
  };
  const deletePost = async () => {
    if (window.confirm("게시글을 삭제하시겠습니까?")) {
      await axios.delete(`http://localhost:8080/board/${id}`);
      alert("삭제되었습니다.");
      navigate("/");
    }
  };
  useEffect(() => {
    getPost();
  }, []);
  return (
    <div>
      <span>제목</span>
      <input type="text" placeholder="제목" value={title} onChange={onChange} />

      <span> 작성자</span>
      <input
        type="text"
        placeholder="작성자"
        value={writer}
        onChange={onChange}
      />
      <br />

      <br />
      <textarea
        placeholder="내용"
        cols="100"
        rows="30"
        value={body}
        onChange={onChange}
      />
      <br />
      <button onClick={updatePost}>수정</button>
      <button onClick={deletePost}>삭제</button>
      <button onClick={backToPost}>취소</button>
    </div>
  );
};

export default PostEditForm;
